<?php$path = realpath(dirname(__FILE__). '/../');include $path.'/config/config.php';__check_login();$error = 0;$response = "F";$message = "กรุณาลองใหม่อีกครั้ง หรือติดต่อโปรแกรมเมอร์";$ck_login = (!empty(__session_user("id"))) ? true : false;if($ck_login){    if(!empty($_GET["paymid"])){        $paymid = $_GET["paymid"];        $download = (!empty( $_GET["download"] )) ? $_GET["download"] : "";            $sql = __invoice_query_select();        $sql .= __invoice_payment_query_from();        $sql .= " WHERE  i.inv_paymid = '".$paymid."'";        $datalist = $db->query($sql)->row();                $invoice_data = __invoice_data("",$paymid);                if($datalist>=1){            if($download==1){                  $pdf = __pdf_payment($datalist,$invoice_data,"D");            }else{                  $pdf = __pdf_payment($datalist,$invoice_data,"I");            }                        $response = $pdf["response"];            $message = $pdf["message"];        }else{            $message = "ไม่พบข้อมูล";            $error = 3;        }    }else{        $message = "กรุณาระบุรหัสใบสำคัญจ่าย";        $error = 2;    }}else{    $message = "กรุณาเข้าสู่ระบบ";    $error = 1;}if($response=="F"){    echo "<center><h1 style='color:red'><br><br>".$message."</h1></center>";}