              $textSTS = 'อนุมัติแล้ว';                    $stySTS = 'font-size: 14px; text-align: center; background-color: #008800; padding: 2px 6px; border-top-left-radius: .5rem; border-bottom-right-radius: .5rem; color: #FFF;';                }            } else {                if ($obj_row["inv_statusMgr"] == 1 && $obj_row["inv_statusCEO"] == 0) {                    $textSTS = 'ตรวจสอบแล้ว<br>รออนุมัติ';                    $stySTS = 'font-size: 14px; text-align: center; background-color: #FF9800; padding: 2px 6px; border-top-left-radius: .5rem; border-bottom-right-radius: .5rem; color: #FFF;';                } else if ($obj_row["inv_statusMgr"] == 1 && $obj_row["inv_statusCEO"] == 1) {                    $textSTS = 'อนุมัติแล้ว';                    $stySTS = 'font-size: 14px; text-align: center; background-color: #008800; padding: 2px 6px; border-top-left-radius: .5rem; border-bottom-right-radius: .5rem; color: #FFF;';                }            }            $str_sql_iv = "SELECT * FROM invoice_tb WHERE inv_paymid = '". $obj_row["paym_id"] ."'";            $obj_rs_iv = mysqli_query($obj_con, $str_sql_iv);            $ivdesc = "";            while ($obj_row_iv = mysqli_fetch_array($obj_rs_iv)) {                $ivdesc = $obj_row_iv['inv_description'] . " || " . $ivdesc;            }                                    $pdf_path = "export/payment_pdf.php?paymid=".$obj_row["paym_id"];            $pdf_view = $pdf_path;            $pdf_download = $pdf_path."&download=1";                        $output .= '<tr>                            <td>                                <div class="truncate-id">                                    '.$obj_row["paym_no"].'                                </div>                            </td>                            <td>                                <div class="truncate">                                    <b>บริษัท : </b> '. $obj_row['paya_name'] .'<br>                                    <b>รายการ : </b> '. $ivdesc .'                                </div>                            </td>                            <td class="text-center">                                '.$obj_row["dep_name"].'                            </td>                            <td>                                <a href="'.$pdf_view.'" target="blank" " rel="noopener noreferrer" class="btn btn-primary form-control mb-1" name="view">                                    <i class="icofont-eye-alt"></i>&nbsp;&nbsp;View                                </a>                                <a  href="'.$pdf_download.'"  target="blank" " rel="noopener noreferrer" class="btn btn-success form-control mb-1">                                    <i class="icofont-download-alt"></i>&nbsp;&nbsp;Downlaod                                </a>                               </td>                            </td>                        </tr>';            $i++;        }                                // <a href="'.$obj_row["paym_file"].'" class="btn btn-primary" style="width: 100%;" target="blank">                                //  <i class="icofont-eye"></i> File                                // </a>    } else {        $output .= '</tbody>                    <tbody>                        <tr>                            <td colspan="5" align="center">ไม่มีข้อมูลใบสำคัญจ่าย</td>                        </tr>                    </tbody>                    <div class="row">';    }    $total_links = ceil($total_data/$limit);    $previous_link = '';    $next_link = '';    $page_link = '';    $output .= '</table>                <div class="row mx-0 my-0">                    <div class="col-md-6 px-0 py-4">                        <ul class="pagination">';    $page_array = array();    if($total_links > 4) {        if($pageno < 5) {            for($count = 1; $count <= 5; $count++) {                $page_array[] = $count;            }            $page_array[] = '...';  