t_link = '<li class="page-item disabled">                                <a class="page-link" href="#">                                    <i class="icofont-rounded-right"></i>                                </a>                            </li>';            } else {                $next_link = '<li class="page-item">                                <a class="page-link" href="javascript:void(0)" data-page_number="'.$next_id.'">                                    <i class="icofont-rounded-right"></i>                                </a>                            </li>';            }        } else {            if($page_array[$count] == '...') {                $page_link .= '<li class="page-item disabled">                                <a class="page-link" href="#">...</a>                            </li> ';            } else {                $page_link .= '<li class="page-item">                                <a class="page-link" href="javascript:void(0)" data-page_number="'.$page_array[$count].'">'.$page_array[$count].'</a>                            </li>';            }        }    }    $output .= $previous_link . $page_link . $next_link;            $output .= '</ul>                    </div>                    <div class="col-md-6 px-0 py-4 text-right">                        <b>จำนวนใบสำคัญจ่ายทั้งหมด : </b><span id="numINV">'.$total_data.'</span>                        <input class="form-control d-none" type="number" id="numINV" value="'.$total_data.'">                    </div>                </div>';    echo $output;?>